import React from 'react';
import { 
  Plane, 
  Ship, 
  Truck, 
  Clock, 
  DollarSign, 
  MapPin 
} from 'lucide-react';

// Transport mode icons mapping
const TRANSPORT_ICONS = {
  'Air': <Plane className="w-6 h-6 text-blue-500" />,
  'Sea': <Ship className="w-6 h-6 text-teal-500" />,
  'Land': <Truck className="w-6 h-6 text-green-500" />
};

// Mock route data (will be replaced with actual API response)
const MOCK_ROUTES = [
  {
    id: 'route-001',
    title: 'Sea-Air Hybrid Route',
    modeSequence: ['Sea', 'Air'],
    estimatedCost: 2840,
    estimatedTransitTime: 14,
    carbonFootprint: 0.75,
    reliabilityScore: 0.85,
    segments: [
      {
        mode: 'Sea',
        startLocation: 'Mumbai',
        endLocation: 'Singapore',
        duration: 7
      },
      {
        mode: 'Air',
        startLocation: 'Singapore',
        endLocation: 'London',
        duration: 7
      }
    ]
  },
  {
    id: 'route-002',
    title: 'Air-Only Direct Route',
    modeSequence: ['Air'],
    estimatedCost: 3200,
    estimatedTransitTime: 10,
    carbonFootprint: 1.2,
    reliabilityScore: 0.92,
    segments: [
      {
        mode: 'Air',
        startLocation: 'Mumbai',
        endLocation: 'London',
        duration: 10
      }
    ]
  }
];

// Route Card Component
const RouteCard = ({ route }) => {
  return (
    <div className="bg-white shadow-md rounded-lg p-6 mb-4 hover:shadow-lg transition-shadow">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold text-gray-800">{route.title}</h3>
        <div className="flex space-x-2">
          {route.modeSequence.map((mode, index) => (
            <div key={index} className="flex items-center">
              {TRANSPORT_ICONS[mode]}
              {index < route.modeSequence.length - 1 && (
                <span className="mx-2 text-gray-400">→</span>
              )}
            </div>
          ))}
        </div>
      </div>

      <div className="grid grid-cols-3 gap-4 text-center">
        <div className="flex items-center justify-center">
          <DollarSign className="mr-2 text-green-600" />
          <span className="font-medium text-gray-700">
            ${route.estimatedCost}
          </span>
        </div>
        <div className="flex items-center justify-center">
          <Clock className="mr-2 text-blue-600" />
          <span className="font-medium text-gray-700">
            {route.estimatedTransitTime} days
          </span>
        </div>
        <div className="flex items-center justify-center">
          <MapPin className="mr-2 text-red-600" />
          <div>
            <span className="font-medium text-gray-700">
              {route.segments[0].startLocation} → {route.segments[route.segments.length - 1].endLocation}
            </span>
          </div>
        </div>
      </div>

      <div className="mt-4 text-sm text-gray-500 flex justify-between">
        <span>
          Carbon Footprint: {route.carbonFootprint} tons CO2
        </span>
        <span>
          Reliability: {(route.reliabilityScore * 100).toFixed(0)}%
        </span>
      </div>
    </div>
  );
};

// Route Output Display Component
const RouteOutputDisplay = ({ routes = MOCK_ROUTES }) => {
  if (routes.length === 0) {
    return (
      <div className="text-center py-10 bg-gray-50 rounded-lg">
        <p className="text-gray-600 text-lg">
          No routes found. Please modify your search criteria.
        </p>
      </div>
    );
  }

  return (
    <div className="bg-gray-50 p-6 rounded-lg">
      <h2 className="text-2xl font-bold mb-6 text-gray-800 flex items-center">
        <Plane className="mr-3 text-blue-600" />
        Route Suggestions
      </h2>
      {routes.map((route) => (
        <RouteCard key={route.id} route={route} />
      ))}
    </div>
  );
};

export default RouteOutputDisplay;
