import React from 'react';
import { Link } from 'react-router-dom';
import { ClipboardCheck, Package, BarChart, FileText, ShieldCheck, Globe, Anchor, Ship, Navigation, MapPin, Compass, TrendingUp } from 'lucide-react';

function HomePage() {
  const features = [
    {
      title: 'Compliance Checker',
      icon: <ClipboardCheck className="h-10 w-10" />,
      description: 'Verify shipment compliance with regulations',
      path: '/clerk',
      color: 'bg-blue-50 hover:bg-blue-100 text-blue-700 border-blue-200'
    },
    {
      title: 'Cross-Border Router',
      icon: <Compass className="h-10 w-10" />,
      description: 'Optimize routes for cost, time and feasibility',
      path: '/route-optimizer',
      color: 'bg-emerald-50 hover:bg-emerald-100 text-emerald-700 border-emerald-200'
    }
  ];

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      {/* Navigation */}
      <nav className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <Anchor className="h-8 w-8 text-blue-600" />
              <span className="ml-2 text-2xl font-bold text-blue-800">Vership</span>
            </div>
            <div className="flex items-center space-x-4">
              <Link to="/" className="px-3 py-2 rounded-md text-sm font-medium text-gray-700 hover:text-blue-600 hover:bg-gray-100">
                Features
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <main className="flex-grow">
        {/* Hero Section */}
        <section className="bg-gradient-to-r from-blue-500 to-emerald-500 py-20 text-white">
          <div className="max-w-4xl mx-auto text-center px-4 sm:px-6 lg:px-8">
            <h1 className="text-4xl font-bold mb-6">
              Global Trade Compliance Made Simple
            </h1>
            <p className="text-xl max-w-3xl mx-auto">
              Automated compliance checks and intelligent routing for cross-border shipments
            </p>
            <div className="mt-8 flex justify-center">
              <Ship className="h-16 w-16 text-white opacity-80" />
            </div>
          </div>
        </section>

        {/* Feature Cards */}
        <section className="py-16 px-4 sm:px-6 lg:px-8 max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-gray-800 mb-12 text-center">Our Solutions</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-10 max-w-5xl mx-auto">
            {features.map((feature, index) => (
              <Link 
                key={index} 
                to={feature.path} 
                className={`flex flex-col items-center p-8 rounded-xl shadow-lg transition-all duration-300 ease-in-out transform hover:-translate-y-2 ${feature.color} border hover:shadow-xl`}
              >
                <div className="p-4 rounded-full mb-6 bg-white shadow-inner">
                  {feature.icon}
                </div>
                <h3 className="text-2xl font-bold mb-4 text-center">
                  {feature.title}
                </h3>
                <p className="text-center text-gray-700 text-lg">
                  {feature.description}
                </p>
              </Link>
            ))}
          </div>
        </section>
      </main>

      <footer className="bg-gray-800 text-white">
        <div className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8 text-center">
          Â© 2025 Vership. All rights reserved.
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
