import React from 'react';
import { Link } from 'react-router-dom';
import { ClipboardCheck, Route, Package, BarChart, Settings, Rocket } from 'lucide-react';

function HomePage() {
  const features = [
    {
      title: 'Compliance Checker',
      icon: <ClipboardCheck className="w-6 h-6 text-blue-500" />,
      description: 'Verify shipment compliance with regulations',
      path: '/clerk'
    },
    {
      title: 'Cross-Border Router',
      icon: <Route className="w-6 h-6 text-blue-500" />,
      description: 'Optimize routes for cost, time and feasibility',
      path: '/route-optimizer'
    }
  ];

  const roles = [
    {
      title: 'Clerk',
      icon: <Package className="w-5 h-5" />,
      path: '/clerk',
    },
    {
      title: 'Manager',
      icon: <BarChart className="w-5 h-5" />,
      path: '/dashboard',
    },
    {
      title: 'Admin',
      icon: <Settings className="w-5 h-5" />,
      path: '/admin',
    }
  ];

  return (
    <div className="min-h-screen bg-white">
      <header className="max-w-5xl mx-auto px-4 py-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Rocket className="w-6 h-6 text-blue-500" />
            <span className="text-xl font-medium text-gray-900">Vership</span>
          </div>
          <nav className="flex space-x-6">
            <a href="#features" className="text-gray-500 hover:text-gray-900 text-sm">Features</a>
            <a href="#roles" className="text-gray-500 hover:text-gray-900 text-sm">Roles</a>
          </nav>
        </div>
      </header>

      <main className="max-w-5xl mx-auto px-4">
        {/* Hero Section */}
        <section className="py-16 text-center">
          <h1 className="text-3xl font-medium text-gray-900 mb-3">
            Global Trade Compliance Made Simple
          </h1>
          <p className="text-lg text-gray-500 mb-16 max-w-2xl mx-auto">
            Automated compliance checks and intelligent routing for cross-border shipments
          </p>
          
          {/* Feature Cards */}
          <div className="grid md:grid-cols-2 gap-6 mb-24" id="features">
            {features.map((feature, index) => (
              <Link 
                key={index}
                to={feature.path}
                className="bg-gray-50 p-6 rounded-lg hover:bg-gray-100 transition-colors duration-200"
              >
                <div className="flex flex-col items-center">
                  {feature.icon}
                  <h3 className="mt-3 text-lg font-medium text-gray-900">{feature.title}</h3>
                  <p className="mt-1 text-gray-500 text-center text-sm">{feature.description}</p>
                </div>
              </Link>
            ))}
          </div>

          {/* Role Selection */}
          <div className="py-8" id="roles">
            <h2 className="text-xl font-medium text-gray-900 mb-6">System Access</h2>
            <div className="flex justify-center space-x-8">
              {roles.map((role, index) => (
                <Link
                  key={index}
                  to={role.path}
                  className="p-4 hover:bg-gray-50 rounded-full transition-colors duration-200 flex flex-col items-center"
                >
                  <div className="w-12 h-12 rounded-full bg-blue-50 flex items-center justify-center">
                    {role.icon}
                  </div>
                  <h3 className="mt-2 text-sm font-medium text-gray-900">{role.title}</h3>
                </Link>
              ))}
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default HomePage;
