import React from 'react';
import { Link } from 'react-router-dom';
import { ClipboardCheck, Ship, Package, BarChart, Settings, Box, Users, FileText, ShieldCheck, Globe } from 'lucide-react';

function HomePage() {
  const features = [
    {
      title: 'Compliance Checker',
      icon: <ShieldCheck className="h-8 w-8" />,
      description: 'Verify shipment compliance with regulations',
      path: '/clerk',
      color: 'bg-amber-50 hover:bg-amber-100 text-amber-800'
    },
    {
      title: 'Cross-Border Router',
      icon: <Globe className="h-8 w-8" />,
      description: 'Optimize routes for cost, time and feasibility',
      path: '/route-optimizer',
      color: 'bg-slate-50 hover:bg-slate-100 text-slate-800'
    }
  ];

  return (
    <div className="min-h-screen flex flex-col bg-stone-50">
      {/* Navigation */}
      <nav className="bg-white shadow-sm border-b border-stone-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <Ship className="h-8 w-8 text-amber-700" />
              <span className="ml-2 text-2xl font-serif text-amber-800">Vership</span>
            </div>
            <div className="flex items-center space-x-4">
              <Link to="/" className="px-3 py-2 text-sm font-medium text-slate-700 hover:text-amber-800">
                Features
              </Link>
            </div>
          </div>
        </div>
      </nav>

      <main className="flex-grow">
        {/* Hero Section */}
        <section className="bg-gradient-to-b from-stone-100 to-stone-50 py-16">
          <div className="max-w-4xl mx-auto text-center px-4 sm:px-6 lg:px-8">
            <h1 className="text-4xl font-serif font-bold text-slate-900 mb-4">
              Global Trade Compliance Made Simple
            </h1>
            <p className="text-xl text-slate-600 max-w-3xl mx-auto">
              Automated compliance checks and intelligent routing for cross-border shipments
            </p>
          </div>
        </section>

        {/* Feature Cards */}
        <section className="py-12 px-4 sm:px-6 lg:px-8 max-w-7xl mx-auto">
          <h2 className="text-2xl font-serif font-semibold text-slate-800 mb-8 text-center">Our Solutions</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8 max-w-5xl mx-auto">
            {features.map((feature, index) => (
              <Link 
                key={index} 
                to={feature.path} 
                className={`flex flex-col items-center p-6 rounded-lg shadow-md transition duration-300 ease-in-out transform hover:scale-105 ${feature.color} border border-stone-200`}
              >
                <div className="p-3 rounded-full mb-4">
                  {feature.icon}
                </div>
                <h3 className="text-xl font-serif font-medium mb-2 text-center">
                  {feature.title}
                </h3>
                <p className="text-center text-slate-700">
                  {feature.description}
                </p>
              </Link>
            ))}
          </div>
        </section>
      </main>

      <footer className="bg-stone-100 border-t border-stone-200">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8 text-center text-stone-600 text-sm">
          © 2025 Vership. All rights reserved.
        </div>
      </footer>
    </div>
  );
}

export default HomePage;
