// frontend/src/components/RouteOptimizer/RouteOptimizerPage.js
import React, { useState } from 'react';
import { Globe, Plane, Ship, Truck, LoaderIcon, AlertTriangle } from 'lucide-react';

// Mock Data for Route Suggestions
const MOCK_ROUTE_SUGGESTIONS = [
  {
    id: 'route-001',
    title: 'Sea-Air Hybrid: Mumbai to London',
    modes: ['Sea', 'Air'],
    estimatedCost: 1500,
    estimatedTransitTime: '14 days',
    routeSegments: [
      {
        mode: 'Sea',
        start: { lat: 19.0760, lng: 72.8777 },  // Mumbai
        end: { lat: 4.5353, lng: 70.3524 },     // Port intermediate point
        estimatedTime: '7 days',
        cost: 800
      },
      {
        mode: 'Air',
        start: { lat: 4.5353, lng: 70.3524 },   // Port intermediate point
        end: { lat: 51.5074, lng: -0.1278 },    // London
        estimatedTime: '7 days',
        cost: 700
      }
    ],
    carbonFootprint: 0.75,
    reliabilityScore: 0.85
  },
  {
    id: 'route-002',
    title: 'Land-Sea Route: Mumbai to Singapore',
    modes: ['Land', 'Sea'],
    estimatedCost: 1200,
    estimatedTransitTime: '12 days',
    routeSegments: [
      {
        mode: 'Land',
        start: { lat: 19.0760, lng: 72.8777 },  // Mumbai
        end: { lat: 22.3193, lng: 114.1694 },   // Hong Kong
        estimatedTime: '5 days',
        cost: 400
      },
      {
        mode: 'Sea',
        start: { lat: 22.3193, lng: 114.1694 }, // Hong Kong
        end: { lat: 1.3521, lng: 103.8198 },    // Singapore
        estimatedTime: '7 days',
        cost: 800
      }
    ],
    carbonFootprint: 0.55,
    reliabilityScore: 0.92
  }
];

// Route Icon Mapping
const ROUTE_MODE_ICONS = {
  Sea: <Ship className="w-5 h-5 text-blue-500" />,
  Air: <Plane className="w-5 h-5 text-teal-500" />,
  Land: <Truck className="w-5 h-5 text-gray-500" />
};

// Route Output Display Component
const RouteOutputDisplay = ({ routes, loading, error }) => {
  if (loading) {
    return (
      <div className="flex justify-center items-center p-6">
        <LoaderIcon className="animate-spin mr-2" />
        <span>Calculating optimal routes...</span>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-4 rounded-lg flex items-center">
        <AlertTriangle className="mr-2 text-red-500" />
        <span className="text-red-700">{error}</span>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <h2 className="text-xl font-semibold mb-4">Route Suggestions</h2>
      {routes.map((route) => (
        <div 
          key={route.id} 
          className="bg-white shadow-md rounded-lg p-6 hover:shadow-lg transition-shadow"
        >
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium text-gray-900">{route.title}</h3>
            <div className="flex space-x-2">
              {route.modes.map((mode, index) => (
                <div key={index} className="flex items-center">
                  {ROUTE_MODE_ICONS[mode]}
                  <span className="ml-1 text-sm text-gray-600">{mode}</span>
                </div>
              ))}
            </div>
          </div>
          
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <p className="text-sm text-gray-500">Estimated Cost</p>
              <p className="font-semibold text-green-600">
                ${route.estimatedCost}
              </p>
            </div>
            <div>
              <p className="text-sm text-gray-500">Transit Time</p>
              <p className="font-semibold text-indigo-600">
                {route.estimatedTransitTime}
              </p>
            </div>
            <div>
              <p className="text-sm text-gray-500">Reliability</p>
              <p className="font-semibold text-yellow-600">
                {(route.reliabilityScore * 100).toFixed(0)}%
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

// Route Map Component (Placeholder with coordinates visualization)
const RouteMap = ({ routes }) => {
  return (
    <div className="bg-gray-100 rounded-lg p-4 h-96 flex items-center justify-center">
      <div className="text-center">
        <Globe className="mx-auto mb-4 w-12 h-12 text-gray-500" />
        <p className="text-gray-600">Route Map Visualization (Coming Soon)</p>
        <p className="text-sm text-gray-500">
          Total Routes: {routes.length}
        </p>
      </div>
    </div>
  );
};

// Route Optimizer Page
const RouteOptimizerPage = () => {
  const [shipmentData, setShipmentData] = useState(null);
  const [routeSuggestions, setRouteSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFormSubmit = (formData) => {
    // Reset previous state
    setError(null);
    setRouteSuggestions([]);
    setLoading(true);

    // Simulate API call with setTimeout
    setTimeout(() => {
      try {
        // In real scenario, this would be an actual API call
        // For now, we're using mock data that matches form input
        const mockRoutes = MOCK_ROUTE_SUGGESTIONS.filter(route => 
          route.title.includes(formData.originCity) && 
          route.title.includes(formData.destinationCity)
        );

        if (mockRoutes.length === 0) {
          throw new Error('No routes found for selected origin and destination');
        }

        setShipmentData(formData);
        setRouteSuggestions(mockRoutes);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    }, 1500);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-6xl mx-auto grid md:grid-cols-3 gap-6">
        {/* Input Form Section */}
        <div className="md:col-span-1">
          <RouteInputForm onSubmit={handleFormSubmit} />
        </div>

        {/* Route Output and Map Section */}
        <div className="md:col-span-2 space-y-6">
          <RouteOutputDisplay 
            routes={routeSuggestions} 
            loading={loading} 
            error={error} 
          />
          <RouteMap routes={routeSuggestions} />
        </div>
      </div>
    </div>
  );
};

export default RouteOptimizerPage;
