import React, { useState } from 'react';
import { FaUserShield } from 'react-icons/fa';

function AdminPanel() {
  const [itemType, setItemType] = useState('');
  const [countries, setCountries] = useState('');
  const [message, setMessage] = useState('');

  const handleAdminSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('/api/admin/update_restricted', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ item_type: itemType, countries }),
      });
      const data = await response.json();
      setMessage(data.message || 'Rule updated successfully!');
      setItemType('');
      setCountries('');
    } catch (error) {
      console.error('Admin update error:', error);
      setMessage('Error updating rule.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <h1 className="text-3xl font-bold text-purple-600 mb-6 flex items-center">
        <FaUserShield className="mr-2" /> Admin Panel
      </h1>
      <div className="bg-white shadow rounded-lg p-6 max-w-lg mx-auto">
        <form onSubmit={handleAdminSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Item Type</label>
            <input
              type="text"
              name="item_type"
              value={itemType}
              onChange={(e) => setItemType(e.target.value)}
              placeholder="e.g., Perfume"
              className="mt-1 block w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">
              Banned Countries (comma-separated)
            </label>
            <input
              type="text"
              name="countries"
              value={countries}
              onChange={(e) => setCountries(e.target.value)}
              placeholder="e.g., Canada,Country X"
              className="mt-1 block w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
          </div>
          <button
            type="submit"
            className="w-full py-2 px-4 bg-purple-600 text-white rounded hover:bg-purple-700 transition"
          >
            Update Rule
          </button>
        </form>
        {message && <p className="mt-4 text-center text-green-600">{message}</p>}
      </div>
    </div>
  );
}

export default AdminPanel;
