import React, { useState, useEffect } from 'react';
import debounce from 'lodash.debounce';

// Country configuration
const COUNTRY_CONFIG = {
  US: { currency: 'USD', valueThreshold: 2500, htsLength: 10 },
  CA: { currency: 'CAD', valueThreshold: 3300, htsLength: 10 },
  DE: { currency: 'EUR', valueThreshold: 1000, htsLength: 8 },
  AE: { currency: 'AED', valueThreshold: 10000, htsLength: 8 }
};

function ComplianceForm() {
  const [formData, setFormData] = useState({
    sender: '',
    recipient: '',
    productDescription: '',
    itemType: '',
    quantity: '',
    weight: '',
    unit: 'kg',
    destination: '',
    declaredValue: '',
    currency: 'USD',
    htsCode: '',
    eccn: '',
    countrySpecificNotes: ''
  });

  const [errors, setErrors] = useState({
    sender: '',
    recipient: '',
    productDescription: '',
    itemType: '',
    quantity: '',
    weight: '',
    destination: '',
    declaredValue: '',
    htsCode: '',
    restrictions: [],
    complianceNotes: []
  });

  const [countryRules, setCountryRules] = useState(null);
  const [touched, setTouched] = useState({});
  const [isChecking, setIsChecking] = useState(false);

  // Real-time validation for individual fields
  const validateField = (name, value) => {
    const rules = {
      sender: val => !!val.trim() || 'Sender is required',
      recipient: val => !!val.trim() || 'Recipient is required',
      productDescription: val => !!val.trim() || 'Product description is required',
      itemType: val => !!val.trim() || 'Item type is required',
      quantity: val => val > 0 || 'Quantity must be positive',
      weight: val => val > 0 || 'Weight must be positive',
      destination: val => !!val.trim() || 'Destination is required',
      declaredValue: val => val > 0 || 'Value must be positive',
      htsCode: val => {
        const expectedLength = COUNTRY_CONFIG[formData.destination]?.htsLength || 10;
        return new RegExp(`^\\d{${expectedLength}}$`).test(val) 
          || `Invalid HTS code (${expectedLength} digits required)`;
      }
    };

    return rules[name] ? rules[name](value) : '';
  };

  // Check country-specific compliance rules
  const checkCountryRules = debounce(async () => {
    if (!formData.destination || !formData.itemType) return;
    
    setIsChecking(true);
    try {
      const response = await fetch(`/api/compliance-check`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          destination: formData.destination,
          itemType: formData.itemType,
          declaredValue: formData.declaredValue
        })
      });

      const { restrictions, requirements } = await response.json();
      
      setErrors(prev => ({
        ...prev,
        restrictions,
        complianceNotes: requirements
      }));

      setCountryRules(requirements);
    } catch (error) {
      console.error('Compliance check failed:', error);
    }
    setIsChecking(false);
  }, 500);

  // Handle field changes
  const handleChange = (name, value) => {
    setTouched(prev => ({ ...prev, [name]: true }));
    setFormData(prev => ({ ...prev, [name]: value }));
    
    // Immediate field validation
    setErrors(prev => ({
      ...prev,
      [name]: validateField(name, value)
    }));

    // Trigger compliance check when relevant fields change
    if (['destination', 'itemType', 'declaredValue'].includes(name)) {
      checkCountryRules();
    }
  };

  // Update currency when destination changes
  useEffect(() => {
    if (formData.destination && COUNTRY_CONFIG[formData.destination]) {
      setFormData(prev => ({
        ...prev,
        currency: COUNTRY_CONFIG[formData.destination].currency
      }));
    }
  }, [formData.destination]);

  // Form submission handler
  const handleSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      console.log('Submitting compliant shipment:', formData);
      // Submit to API
    }
  };

  // Check overall form validity
  const isFormValid = Object.values(errors).every(error => {
    if (Array.isArray(error)) return error.length === 0;
    return !error;
  });

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-xl">
      <h1 className="text-2xl font-bold mb-6 text-blue-800">
        Export Compliance Verification
      </h1>

      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Sender/Receiver Section */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Parties Information</h2>
          
          <InputField
            label="Sender Name"
            name="sender"
            value={formData.sender}
            onChange={handleChange}
            error={errors.sender}
            touched={touched.sender}
          />

          <InputField
            label="Recipient Address"
            name="recipient"
            value={formData.recipient}
            onChange={handleChange}
            error={errors.recipient}
            touched={touched.recipient}
            textarea
          />
        </section>

        {/* Product Details Section */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Product Details</h2>

          <InputField
            label="Product Description"
            name="productDescription"
            value={formData.productDescription}
            onChange={handleChange}
            error={errors.productDescription}
            touched={touched.productDescription}
            textarea
          />

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <SelectField
              label="Item Type"
              name="itemType"
              value={formData.itemType}
              options={[
                'Electronics',
                'Pharmaceuticals',
                'Lithium Batteries',
                'Perfume',
                'Chemicals'
              ]}
              onChange={handleChange}
              error={errors.itemType}
              touched={touched.itemType}
            />

            <InputField
              label="Quantity"
              name="quantity"
              type="number"
              value={formData.quantity}
              onChange={handleChange}
              error={errors.quantity}
              touched={touched.quantity}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <InputField
              label="Weight"
              name="weight"
              type="number"
              value={formData.weight}
              onChange={handleChange}
              error={errors.weight}
              touched={touched.weight}
              suffix={
                <select
                  name="unit"
                  value={formData.unit}
                  onChange={(e) => handleChange('unit', e.target.value)}
                  className="ml-2 p-2 border rounded"
                >
                  <option value="kg">kg</option>
                  <option value="lbs">lbs</option>
                </select>
              }
            />

            <SelectField
              label="Destination Country"
              name="destination"
              value={formData.destination}
              options={Object.entries(COUNTRY_CONFIG).map(([code, config]) => ({
                value: code,
                label: code === 'US' ? 'United States' :
                       code === 'CA' ? 'Canada' :
                       code === 'DE' ? 'Germany' :
                       'United Arab Emirates'
              }))}
              onChange={handleChange}
              error={errors.destination}
              touched={touched.destination}
            />
          </div>
        </section>

        {/* Commercial Information Section */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Commercial Information</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <InputField
              label="Declared Value"
              name="declaredValue"
              type="number"
              value={formData.declaredValue}
              onChange={handleChange}
              error={errors.declaredValue}
              touched={touched.declaredValue}
              prefix={
                <span className="mr-2">
                  {formData.currency}
                </span>
              }
            />

            <InputField
              label="HTS Code"
              name="htsCode"
              value={formData.htsCode}
              onChange={handleChange}
              error={errors.htsCode}
              touched={touched.htsCode}
              placeholder={`Enter ${COUNTRY_CONFIG[formData.destination]?.htsLength || 10}-digit code`}
            />
          </div>

          <InputField
            label="ECCN (Export Control Classification Number)"
            name="eccn"
            value={formData.eccn}
            onChange={handleChange}
            placeholder="Enter ECCN if applicable"
          />
        </section>

        {/* Compliance Feedback Section */}
        <section className="space-y-4">
          {isChecking && (
            <div className="p-4 bg-blue-50 text-blue-800 rounded-lg">
              Checking international compliance regulations...
            </div>
          )}

          {errors.restrictions.length > 0 && (
            <div className="p-4 bg-red-50 text-red-800 rounded-lg">
              <h3 className="font-semibold mb-2">Compliance Restrictions:</h3>
              <ul className="list-disc pl-6">
                {errors.restrictions.map((error, index) => (
                  <li key={index}>{error}</li>
                ))}
              </ul>
            </div>
          )}

          {countryRules && (
            <div className="p-4 bg-yellow-50 text-yellow-800 rounded-lg">
              <h3 className="font-semibold mb-2">Country Requirements:</h3>
              <ul className="list-disc pl-6">
                {countryRules.map((rule, index) => (
                  <li key={index}>{rule}</li>
                ))}
              </ul>
            </div>
          )}

          {isFormValid && !errors.restrictions.length && (
            <div className="p-4 bg-green-50 text-green-800 rounded-lg">
              âœ“ Shipment complies with all regulations
            </div>
          )}
        </section>

        <button
          type="submit"
          disabled={!isFormValid || isChecking}
          className={`w-full py-3 px-6 rounded-lg font-semibold transition-colors
            ${isFormValid && !isChecking 
              ? 'bg-green-600 hover:bg-green-700 text-white' 
              : 'bg-gray-300 text-gray-500 cursor-not-allowed'}
          `}
        >
          {isChecking ? 'Validating...' : 'Submit Shipment'}
        </button>
      </form>
    </div>
  );
}

// Reusable Input Component
const InputField = ({ 
  label, 
  name, 
  value, 
  onChange, 
  error, 
  touched, 
  type = 'text', 
  textarea = false, 
  prefix, 
  suffix, 
  ...props 
}) => (
  <div>
    <label className="block text-sm font-medium text-gray-700 mb-1">
      {label}
      {props.required && <span className="text-red-500">*</span>}
    </label>
    <div className="flex items-center gap-2">
      {prefix && <span>{prefix}</span>}
      {textarea ? (
        <textarea
          name={name}
          value={value}
          onChange={(e) => onChange(name, e.target.value)}
          className={`w-full p-2 border rounded-md focus:ring-2 ${
            error && touched 
              ? 'border-red-500 focus:ring-red-200' 
              : 'border-gray-300 focus:ring-blue-200'
          }`}
          rows="3"
          {...props}
        />
      ) : (
        <input
          type={type}
          name={name}
          value={value}
          onChange={(e) => onChange(name, e.target.value)}
          className={`w-full p-2 border rounded-md focus:ring-2 ${
            error && touched 
              ? 'border-red-500 focus:ring-red-200' 
              : 'border-gray-300 focus:ring-blue-200'
          }`}
          {...props}
        />
      )}
      {suffix && <span>{suffix}</span>}
    </div>
    {error && touched && (
      <p className="mt-1 text-sm text-red-600">{error}</p>
    )}
  </div>
);

// Reusable Select Component
const SelectField = ({ label, name, value, options, onChange, error, touched }) => (
  <div>
    <label className="block text-sm font-medium text-gray-700 mb-1">
      {label}
    </label>
    <select
      name={name}
      value={value}
      onChange={(e) => onChange(name, e.target.value)}
      className={`w-full p-2 border rounded-md focus:ring-2 ${
        error && touched 
          ? 'border-red-500 focus:ring-red-200' 
          : 'border-gray-300 focus:ring-blue-200'
      }`}
    >
      <option value="">Select {label}</option>
      {options.map((option, index) => (
        <option key={index} value={option.value || option}>
          {option.label || option}
        </option>
      ))}
    </select>
    {error && touched && (
      <p className="mt-1 text-sm text-red-600">{error}</p>
    )}
  </div>
);

export default ComplianceForm;
