// form.js
import React, { useEffect } from 'react';
import { useForm } from 'react-hook-form';
import debounce from 'lodash.debounce';

// Country configuration (codes used in parcels table)
const COUNTRY_CONFIG = {
  US: { currency: 'USD', valueThreshold: 2500, htsLength: 10 },
  CA: { currency: 'CAD', valueThreshold: 3300, htsLength: 10 },
  DE: { currency: 'EUR', valueThreshold: 1000, htsLength: 8 },
  AE: { currency: 'AED', valueThreshold: 10000, htsLength: 8 }
};

// Map country codes to full names (for compliance-check)
const COUNTRY_MAP = {
  US: 'United States',
  CA: 'Canada',
  DE: 'Germany',
  AE: 'United Arab Emirates'
};

const PURPOSE_OPTIONS = [
  { value: 'Commercial', label: 'Commercial' },
  { value: 'Gift', label: 'Gift' },
  { value: 'Documents', label: 'Documents' },
  { value: 'Personal Effects', label: 'Personal Effects' },
  { value: 'Sample', label: 'Sample' }
];

const SHIPPING_TERMS_OPTIONS = [
  { value: 'DAP', label: 'DAP' },
  { value: 'DDP', label: 'DDP' },
  { value: 'EXW', label: 'EXW' },
  { value: 'FOB', label: 'FOB' }
];

const ORIGIN_COUNTRY_OPTIONS = Object.entries(COUNTRY_CONFIG).map(([code]) => ({
  value: code,
  label:
    code === 'US'
      ? 'United States'
      : code === 'CA'
      ? 'Canada'
      : code === 'DE'
      ? 'Germany'
      : 'United Arab Emirates'
}));

function ComplianceForm({ onSubmitSuccess }) {
  const {
    register,
    handleSubmit,
    watch,
    setValue,
    formState: { errors }
  } = useForm({
    defaultValues: {
      sender: '',
      recipient: '',
      productDescription: '',
      itemType: '',
      quantity: '',
      weight: '',
      unit: 'kg',
      destination: '',
      declaredValue: '',
      currency: 'USD',
      htsCode: '',
      eccn: '',
      countrySpecificNotes: '',
      purpose: '',
      shippingTerms: '',
      originCountry: '',
      senderTax: '',
      recipientTax: '',
      unNumber: '',
      packagingType: ''
    }
  });

  // Watch fields for conditional rendering and compliance check trigger
  const watchDestination = watch('destination');
  const watchItemType = watch('itemType');
  const watchDeclaredValue = watch('declaredValue');
  const watchPurpose = watch('purpose');

  // Debounced function to check compliance rules from backend
  const checkCountryRules = debounce(async (destination, itemType, declaredValue) => {
    if (!destination || !itemType) return;
    try {
      const response = await fetch('http://localhost:3000/api/compliance-check', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // Use the full country name for compliance-check
        body: JSON.stringify({
          destination: COUNTRY_MAP[destination],
          itemType,
          declaredValue
        })
      });
      const { restrictions, requirements } = await response.json();
      // Here we store the requirements in a hidden field so it becomes part of the payload.
      setValue('complianceNotes', requirements.join(' | '));
      // Optionally, you could store restrictions in state if needed.
      setValue('restrictions', restrictions.join(' | '));
    } catch (error) {
      console.error('Compliance check failed:', error);
    }
  }, 500);

  // Trigger compliance check when destination, itemType, or declaredValue changes
  useEffect(() => {
    checkCountryRules(watchDestination, watchItemType, watchDeclaredValue);
  }, [watchDestination, watchItemType, watchDeclaredValue]);

  // Form submission handler
  const onSubmit = async (data) => {
    // Determine shipment status based on compliance notes (if any restrictions, mark as Rejected)
    const status = data.restrictions ? 'Rejected' : 'Compliant';
    // Build payload matching the parcels table (only core fields)
    const payload = {
      sender: data.sender,
      recipient_address: data.recipient,
      item_type: data.itemType,
      weight: parseFloat(data.weight),
      destination: data.destination, // store country code
      declared_value: parseFloat(data.declaredValue),
      hts_code: data.htsCode,
      status,
      compliance_notes: data.complianceNotes || ''
    };

    console.log('Submitting payload:', payload);
    try {
      const response = await fetch('http://localhost:3000/api/parcels', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      if (!response.ok) {
        const errorResponse = await response.text();
        throw new Error(errorResponse || 'Failed to submit shipment. Please try again.');
      }
      const result = await response.json();
      console.log('Shipment submitted:', result);
      if (onSubmitSuccess) {
        onSubmitSuccess(result);
      }
    } catch (error) {
      console.error(error);
      alert(error.message);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-xl">
      <h1 className="text-2xl font-bold mb-6 text-blue-800">Export Compliance Verification</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Parties Information */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Parties Information</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Sender Name</label>
            <input
              type="text"
              {...register('sender', { required: 'Sender is required' })}
              className="w-full p-2 border rounded-md"
            />
            {errors.sender && <p className="text-red-600">{errors.sender.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Recipient Address</label>
            <textarea
              {...register('recipient', { required: 'Recipient is required' })}
              className="w-full p-2 border rounded-md"
            />
            {errors.recipient && <p className="text-red-600">{errors.recipient.message}</p>}
          </div>
        </section>

        {/* Product Details */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Product Details</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Product Description</label>
            <textarea
              {...register('productDescription', { required: 'Product description is required' })}
              className="w-full p-2 border rounded-md"
            />
            {errors.productDescription && <p className="text-red-600">{errors.productDescription.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Item Type</label>
            <select
              {...register('itemType', { required: 'Item type is required' })}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Select Item Type</option>
              <option value="Electronics">Electronics</option>
              <option value="Pharmaceuticals">Pharmaceuticals</option>
              <option value="Lithium Batteries">Lithium Batteries</option>
              <option value="Perfume">Perfume</option>
              <option value="Chemicals">Chemicals</option>
              <option value="Dangerous Goods">Dangerous Goods</option>
            </select>
            {errors.itemType && <p className="text-red-600">{errors.itemType.message}</p>}
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Quantity</label>
              <input
                type="number"
                {...register('quantity', { min: { value: 1, message: 'Quantity must be positive' } })}
                className="w-full p-2 border rounded-md"
              />
              {errors.quantity && <p className="text-red-600">{errors.quantity.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Weight</label>
              <input
                type="number"
                {...register('weight', {
                  required: 'Weight is required',
                  min: { value: 0.1, message: 'Weight must be positive' }
                })}
                className="w-full p-2 border rounded-md"
              />
              {errors.weight && <p className="text-red-600">{errors.weight.message}</p>}
            </div>
          </div>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Unit</label>
              <select {...register('unit')} className="w-full p-2 border rounded-md">
                <option value="kg">kg</option>
                <option value="lbs">lbs</option>
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Destination Country</label>
              <select
                {...register('destination', { required: 'Destination is required' })}
                className="w-full p-2 border rounded-md"
              >
                <option value="">Select Destination Country</option>
                {Object.entries(COUNTRY_CONFIG).map(([code]) => (
                  <option key={code} value={code}>
                    {code === 'US'
                      ? 'United States'
                      : code === 'CA'
                      ? 'Canada'
                      : code === 'DE'
                      ? 'Germany'
                      : 'United Arab Emirates'}
                  </option>
                ))}
              </select>
              {errors.destination && <p className="text-red-600">{errors.destination.message}</p>}
            </div>
          </div>
        </section>

        {/* Commercial Information & New Fields */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Commercial Information</h2>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Declared Value</label>
              <div className="flex">
                <span className="mr-2">
                  {COUNTRY_CONFIG[watchDestination]?.currency || 'USD'}
                </span>
                <input
                  type="number"
                  {...register('declaredValue', {
                    required: 'Declared value is required',
                    min: { value: 1, message: 'Value must be positive' }
                  })}
                  className="w-full p-2 border rounded-md"
                />
              </div>
              {errors.declaredValue && (
                <p className="text-red-600">{errors.declaredValue.message}</p>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">HTS Code</label>
              <input
                type="text"
                {...register('htsCode', {
                  required: 'HTS Code is required',
                  pattern: {
                    value: new RegExp(`^\\d{${COUNTRY_CONFIG[watchDestination]?.htsLength || 10}}$`),
                    message: `Invalid HTS code`
                  }
                })}
                className="w-full p-2 border rounded-md"
              />
              {errors.htsCode && <p className="text-red-600">{errors.htsCode.message}</p>}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Purpose of Shipment</label>
            <select
              {...register('purpose', { required: 'Purpose of Shipment is required' })}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Select Purpose</option>
              {PURPOSE_OPTIONS.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
            {errors.purpose && <p className="text-red-600">{errors.purpose.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Shipping Terms</label>
            <select
              {...register('shippingTerms', { required: 'Shipping Terms is required' })}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Select Shipping Terms</option>
              {SHIPPING_TERMS_OPTIONS.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
            {errors.shippingTerms && <p className="text-red-600">{errors.shippingTerms.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Origin Country</label>
            <select
              {...register('originCountry', { required: 'Origin Country is required' })}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Select Origin Country</option>
              {ORIGIN_COUNTRY_OPTIONS.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.label}
                </option>
              ))}
            </select>
            {errors.originCountry && <p className="text-red-600">{errors.originCountry.message}</p>}
          </div>
          {watchPurpose === 'Commercial' && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Sender's Tax/EIN Number
                </label>
                <input
                  type="text"
                  {...register('senderTax', { required: 'Tax/EIN Number is required for commercial shipments' })}
                  className="w-full p-2 border rounded-md"
                />
                {errors.senderTax && <p className="text-red-600">{errors.senderTax.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Recipient's Tax/VAT Number
                </label>
                <input
                  type="text"
                  {...register('recipientTax', { required: 'Tax/VAT Number is required for commercial shipments' })}
                  className="w-full p-2 border rounded-md"
                />
                {errors.recipientTax && <p className="text-red-600">{errors.recipientTax.message}</p>}
              </div>
            </>
          )}
          {watchItemType === 'Dangerous Goods' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">UN Number</label>
              <input
                type="text"
                {...register('unNumber', { required: 'UN Number is required for Dangerous Goods' })}
                className="w-full p-2 border rounded-md"
              />
              {errors.unNumber && <p className="text-red-600">{errors.unNumber.message}</p>}
            </div>
          )}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Packaging Type</label>
            <select
              {...register('packagingType', { required: 'Packaging Type is required' })}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Select Packaging Type</option>
              <option value="Standard Box">Standard Box</option>
              <option value="Fragile Packaging">Fragile Packaging</option>
              <option value="Temperature Controlled">Temperature Controlled</option>
              <option value="Special Packaging Required">Special Packaging Required</option>
            </select>
            {errors.packagingType && <p className="text-red-600">{errors.packagingType.message}</p>}
          </div>
        </section>

        {/* Compliance Feedback Section */}
        <section className="space-y-4">
          {/* Optionally display compliance results if desired */}
          {/* For example, you could show the stored complianceNotes */}
          {watch('complianceNotes') && (
            <div className="p-4 bg-green-50 text-green-800 rounded-lg">
               {watch('complianceNotes')}
            </div>
          )}
        </section>

        <button
          type="submit"
          className="w-full py-3 px-6 rounded-lg font-semibold transition-colors bg-green-600 hover:bg-green-700 text-white"
        >
          Submit Shipment
        </button>
      </form>
    </div>
  );
}

export default ComplianceForm;
