import React from 'react';
import { MapContainer, TileLayer, Polyline, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});

// Mock route data with coordinates
const MOCK_ROUTE = {
  segments: [
    {
      mode: 'Sea',
      start: { lat: 19.0760, lng: 72.8777 },   // Mumbai
      end: { lat: 1.3521, lng: 103.8198 },     // Singapore
      color: 'blue'
    },
    {
      mode: 'Air',
      start: { lat: 1.3521, lng: 103.8198 },   // Singapore
      end: { lat: 51.5074, lng: -0.1278 },     // London
      color: 'red'
    }
  ],
  origin: { lat: 19.0760, lng: 72.8777 },      // Mumbai
  destination: { lat: 51.5074, lng: -0.1278 }  // London
};

const RouteMap = ({ route = MOCK_ROUTE }) => {
  // Calculate center point for the map
  const centerLat = (route.origin.lat + route.destination.lat) / 2;
  const centerLng = (route.origin.lng + route.destination.lng) / 2;

  return (
    <div className="w-full h-[500px] rounded-lg overflow-hidden shadow-lg">
      <MapContainer 
        center={[centerLat, centerLng]} 
        zoom={4} 
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        {/* Origin Marker */}
        <Marker position={[route.origin.lat, route.origin.lng]}>
          <Popup>Origin: Mumbai</Popup>
        </Marker>

        {/* Destination Marker */}
        <Marker position={[route.destination.lat, route.destination.lng]}>
          <Popup>Destination: London</Popup>
        </Marker>

        {/* Route Segments */}
        {route.segments.map((segment, index) => (
          <Polyline
            key={index}
            positions={[
              [segment.start.lat, segment.start.lng],
              [segment.end.lat, segment.end.lng]
            ]}
            color={segment.color}
            weight={5}
            opacity={0.7}
          />
        ))}
      </MapContainer>
    </div>
  );
};

export default RouteMap;
