// form.js
import React, { useState, useEffect } from 'react';
import { useForm, useWatch } from 'react-hook-form';
import debounce from 'lodash.debounce';

// Country configuration (codes used in parcels table)
const COUNTRY_CONFIG = {
  US: { currency: 'USD', valueThreshold: 2500, htsLength: 10 },
  CA: { currency: 'CAD', valueThreshold: 3300, htsLength: 10 },
  DE: { currency: 'EUR', valueThreshold: 1000, htsLength: 8 },
  AE: { currency: 'AED', valueThreshold: 10000, htsLength: 8 }
};

// Map country codes to full names (for compliance-check)
const COUNTRY_MAP = {
  US: 'United States',
  CA: 'Canada',
  DE: 'Germany',
  AE: 'United Arab Emirates'
};

const PURPOSE_OPTIONS = [
  "Commercial",
  "Gift",
  "Documents",
  "Personal Effects",
  "Sample"
];

const SHIPPING_TERMS_OPTIONS = [
  "DAP",
  "DDP",
  "EXW",
  "FOB",
  "Duties Paid by Sender",
  "Duties Paid by Receiver"
];

const ORIGIN_COUNTRY_OPTIONS = [
  { value: 'US', label: 'United States' },
  { value: 'CA', label: 'Canada' },
  { value: 'DE', label: 'Germany' },
  { value: 'AE', label: 'United Arab Emirates' }
  // Add more if needed
];

function ComplianceForm({ onSubmitSuccess }) {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isValid }
  } = useForm({
    mode: "onChange",
    defaultValues: {
      sender: '',
      recipient: '',
      productDescription: '',
      itemType: '',
      quantity: '',
      weight: '',
      unit: 'kg',
      destination: '',
      declaredValue: '',
      htsCode: '',
      purposeOfShipment: '',
      shippingTerms: '',
      originCountry: '',
      senderTax: '',
      recipientTax: ''
    }
  });

  // State for compliance check results
  const [countryRules, setCountryRules] = useState(null);
  const [isChecking, setIsChecking] = useState(false);
  const [submissionError, setSubmissionError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  // Watch fields needed for compliance check
  const destination = watch("destination");
  const itemType = watch("itemType");
  const declaredValue = watch("declaredValue");

  // Debounced compliance check call
  const checkCompliance = debounce(async () => {
    if (!destination || !itemType) return;
    setIsChecking(true);
    try {
      const response = await fetch('http://localhost:3000/api/compliance-check', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        // For compliance check, send the full country name
        body: JSON.stringify({
          destination: COUNTRY_MAP[destination],
          itemType,
          declaredValue
        })
      });
      const { restrictions, requirements } = await response.json();
      // For simplicity, we use requirements as our countryRules
      setCountryRules(requirements);
    } catch (error) {
      console.error('Compliance check failed:', error);
    }
    setIsChecking(false);
  }, 500);

  useEffect(() => {
    checkCompliance();
  }, [destination, itemType, declaredValue]);

  // Watch purpose to conditionally render tax fields
  const purposeOfShipment = watch("purposeOfShipment");

  const onSubmit = async (data) => {
    setSubmissionError(null);
    setSubmitting(true);
    try {
      // Determine compliance status based on countryRules from compliance-check
      const status = (countryRules && countryRules.length > 0) ? 'Rejected' : 'Compliant';
      const complianceNotes = countryRules ? countryRules.join(' | ') : '';

      // Build payload matching the parcels table in your database.
      // Note: we send the destination code (e.g. 'US') while compliance-check uses the full name.
      const payload = {
        sender: data.sender,
        recipient_address: data.recipient,
        item_type: data.itemType,
        weight: parseFloat(data.weight),
        destination: data.destination,
        declared_value: parseFloat(data.declaredValue),
        hts_code: data.htsCode,
        status,
        compliance_notes: complianceNotes
      };

      console.log('Submitting payload:', payload);
      
      const response = await fetch('http://localhost:3000/api/parcels', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      
      if (!response.ok) {
        const errorResponse = await response.text();
        console.error('Backend error:', errorResponse);
        throw new Error('Failed to submit shipment. Please try again.');
      }
      
      const result = await response.json();
      console.log('Shipment submitted:', result);
      if (onSubmitSuccess) {
        onSubmitSuccess(result);
      }
    } catch (error) {
      console.error(error);
      setSubmissionError(error.message);
    }
    setSubmitting(false);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-lg shadow-xl">
      <h1 className="text-2xl font-bold mb-6 text-blue-800">Export Compliance Verification</h1>
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Parties Information */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Parties Information</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Sender Name</label>
            <input {...register("sender", { required: "Sender is required" })} className="w-full p-2 border rounded-md" />
            {errors.sender && <p className="text-red-600 text-sm">{errors.sender.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Recipient Address</label>
            <textarea {...register("recipient", { required: "Recipient is required" })} className="w-full p-2 border rounded-md" rows="3" />
            {errors.recipient && <p className="text-red-600 text-sm">{errors.recipient.message}</p>}
          </div>
        </section>
        
        {/* Product Details */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Product Details</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Product Description</label>
            <textarea {...register("productDescription", { required: "Product description is required" })} className="w-full p-2 border rounded-md" rows="3" />
            {errors.productDescription && <p className="text-red-600 text-sm">{errors.productDescription.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Item Type</label>
            <select {...register("itemType", { required: "Item type is required" })} className="w-full p-2 border rounded-md">
              <option value="">Select Item Type</option>
              <option value="Electronics">Electronics</option>
              <option value="Pharmaceuticals">Pharmaceuticals</option>
              <option value="Lithium Batteries">Lithium Batteries</option>
              <option value="Perfume">Perfume</option>
              <option value="Chemicals">Chemicals</option>
            </select>
            {errors.itemType && <p className="text-red-600 text-sm">{errors.itemType.message}</p>}
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700">Quantity</label>
              <input type="number" {...register("quantity", { required: "Quantity is required", min: { value: 1, message: "Must be positive" } })} className="w-full p-2 border rounded-md" />
              {errors.quantity && <p className="text-red-600 text-sm">{errors.quantity.message}</p>}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Weight</label>
              <input type="number" {...register("weight", { required: "Weight is required", min: { value: 0.1, message: "Must be positive" } })} className="w-full p-2 border rounded-md" />
              {errors.weight && <p className="text-red-600 text-sm">{errors.weight.message}</p>}
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Unit</label>
            <select {...register("unit")} className="w-full p-2 border rounded-md">
              <option value="kg">kg</option>
              <option value="lbs">lbs</option>
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Destination Country</label>
            <select {...register("destination", { required: "Destination is required" })} className="w-full p-2 border rounded-md">
              <option value="">Select Destination</option>
              {Object.entries(COUNTRY_CONFIG).map(([code]) => (
                <option key={code} value={code}>
                  {code === 'US' ? 'United States' : code === 'CA' ? 'Canada' : code === 'DE' ? 'Germany' : 'United Arab Emirates'}
                </option>
              ))}
            </select>
            {errors.destination && <p className="text-red-600 text-sm">{errors.destination.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Declared Value</label>
            <input type="number" {...register("declaredValue", { required: "Declared value is required", min: { value: 0.1, message: "Must be positive" } })} className="w-full p-2 border rounded-md" />
            {errors.declaredValue && <p className="text-red-600 text-sm">{errors.declaredValue.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">HTS Code</label>
            <input type="text" {...register("htsCode", { required: "HTS Code is required", pattern: { value: /^\d{10}$/, message: "Must be 10 digits" } })} className="w-full p-2 border rounded-md" placeholder={`Enter ${COUNTRY_CONFIG[watch("destination")]?.htsLength || 10}-digit code`} />
            {errors.htsCode && <p className="text-red-600 text-sm">{errors.htsCode.message}</p>}
          </div>
        </section>
        
        {/* Additional Shipment Details */}
        <section className="space-y-4 p-4 bg-gray-50 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Additional Shipment Details</h2>
          <div>
            <label className="block text-sm font-medium text-gray-700">Purpose of Shipment</label>
            <select {...register("purposeOfShipment", { required: "Purpose of shipment is required" })} className="w-full p-2 border rounded-md">
              <option value="">Select Purpose</option>
              {PURPOSE_OPTIONS.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
            {errors.purposeOfShipment && <p className="text-red-600 text-sm">{errors.purposeOfShipment.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Shipping Terms (Incoterms)</label>
            <select {...register("shippingTerms", { required: "Shipping terms are required" })} className="w-full p-2 border rounded-md">
              <option value="">Select Shipping Terms</option>
              {SHIPPING_TERMS_OPTIONS.map(option => (
                <option key={option} value={option}>{option}</option>
              ))}
            </select>
            {errors.shippingTerms && <p className="text-red-600 text-sm">{errors.shippingTerms.message}</p>}
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Origin Country</label>
            <select {...register("originCountry", { required: "Origin country is required" })} className="w-full p-2 border rounded-md">
              <option value="">Select Origin Country</option>
              {ORIGIN_COUNTRY_OPTIONS.map(option => (
                <option key={option.value} value={option.value}>{option.label}</option>
              ))}
            </select>
            {errors.originCountry && <p className="text-red-600 text-sm">{errors.originCountry.message}</p>}
          </div>
          {purposeOfShipment === "Commercial" && (
            <>
              <div>
                <label className="block text-sm font-medium text-gray-700">Sender's Tax/EIN Number</label>
                <input type="text" {...register("senderTax", { required: "Tax/EIN is required for commercial shipments" })} className="w-full p-2 border rounded-md" />
                {errors.senderTax && <p className="text-red-600 text-sm">{errors.senderTax.message}</p>}
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Recipient's Tax/VAT Number</label>
                <input type="text" {...register("recipientTax")} className="w-full p-2 border rounded-md" />
              </div>
            </>
          )}
          {/* Additional fields like UN Number or Packaging can be added later */}
        </section>
        
        {/* Compliance Feedback */}
        <section className="space-y-4">
          {isChecking && (
            <div className="p-4 bg-blue-50 text-blue-800 rounded-lg">
              Checking international compliance regulations...
            </div>
          )}
          {countryRules && countryRules.length > 0 && (
            <div className="p-4 bg-yellow-50 text-yellow-800 rounded-lg">
              <h3 className="font-semibold mb-2">Country Requirements:</h3>
              <ul className="list-disc pl-6">
                {countryRules.map((rule, index) => (
                  <li key={index}>{rule}</li>
                ))}
              </ul>
            </div>
          )}
          {!isChecking && (!countryRules || countryRules.length === 0) && (
            <div className="p-4 bg-green-50 text-green-800 rounded-lg">
              ✓ Shipment complies with all regulations
            </div>
          )}
          {submissionError && (
            <div className="p-4 bg-red-100 text-red-800 rounded-lg">
              {submissionError}
            </div>
          )}
        </section>
        
        <button
          type="submit"
          disabled={!isValid || isChecking || submitting}
          className={`w-full py-3 px-6 rounded-lg font-semibold transition-colors ${
            isValid && !isChecking && !submitting
              ? 'bg-green-600 hover:bg-green-700 text-white'
              : 'bg-gray-300 text-gray-500 cursor-not-allowed'
          }`}
        >
          {submitting ? 'Submitting...' : 'Submit Shipment'}
        </button>
      </form>
    </div>
  );
}

export default ComplianceForm;
