import React from 'react';
import { Link } from 'react-router-dom';
import { ClipboardCheck, Route, Package, Settings, BarChart, Rocket } from 'lucide-react';

function HomePage() {
  const features = [
    {
      title: 'Compliance Checker',
      icon: <ClipboardCheck className="w-8 h-8 text-blue-600" />,
      description: 'Real-time verification of shipment compliance with international regulations',
      path: '/clerk'
    },
    {
      title: 'Cross-Border Router',
      icon: <Route className="w-8 h-8 text-emerald-600" />,
      description: 'Optimal route suggestions based on cost, time, and border feasibility',
      path: '/route-optimizer'
    }
  ];

  const roles = [
    {
      title: 'Clerk',
      icon: <Package className="w-6 h-6 text-blue-600" />,
      path: '/clerk',
      color: 'bg-blue-50 border-blue-200 hover:border-blue-300'
    },
    {
      title: 'Manager',
      icon: <BarChart className="w-6 h-6 text-emerald-600" />,
      path: '/dashboard',
      color: 'bg-emerald-50 border-emerald-200 hover:border-emerald-300'
    },
    {
      title: 'Admin',
      icon: <Settings className="w-6 h-6 text-purple-600" />,
      path: '/admin',
      color: 'bg-purple-50 border-purple-200 hover:border-purple-300'
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      <header className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Rocket className="w-8 h-8 text-blue-600" />
            <span className="text-2xl font-bold text-gray-900">Vership</span>
          </div>
          <nav className="flex space-x-4">
            <a href="#features" className="text-gray-600 hover:text-gray-900">Features</a>
            <a href="#roles" className="text-gray-600 hover:text-gray-900">Roles</a>
          </nav>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Hero Section */}
        <section className="text-center py-20">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            Global Trade Compliance Made Simple
          </h1>
          <p className="text-xl text-gray-600 mb-8">
            Automated compliance checks and intelligent routing for seamless cross-border shipments
          </p>
          
          {/* Feature Cards */}
          <div className="grid md:grid-cols-2 gap-8 mb-20" id="features">
            {features.map((feature, index) => (
              <Link 
                key={index}
                to={feature.path}
                className="bg-white p-8 rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-200"
              >
                <div className="flex flex-col items-center">
                  {feature.icon}
                  <h3 className="mt-4 text-xl font-semibold text-gray-900">{feature.title}</h3>
                  <p className="mt-2 text-gray-600 text-center">{feature.description}</p>
                </div>
              </Link>
            ))}
          </div>

          {/* Role Selection */}
          <div className="py-12" id="roles">
            <h2 className="text-2xl font-bold text-gray-900 mb-8">System Access</h2>
            <div className="grid md:grid-cols-3 gap-6">
              {roles.map((role, index) => (
                <Link
                  key={index}
                  to={role.path}
                  className={`${role.color} p-6 rounded-lg border-2 transition-colors duration-200 flex flex-col items-center`}
                >
                  {role.icon}
                  <h3 className="mt-4 text-lg font-medium text-gray-900">{role.title}</h3>
                </Link>
              ))}
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default HomePage;
